mapper.select:
select

from finance_records
where financename = #{financename,jdbcType=VARCHAR} and financeday = #{financeday,jdbcType=VARCHAR} and usercode = #{usercode,jdbcType=VARCHAR}

select.if:
select sum(financemoney) financemoney,financeday,financetype from finance_records where usercode = #{param.usercode,jdbcType=VARCHAR} GROUP BY
financeday,financetype order by
financeday ${param.order}

select sum(financemoney) financemoney,financemonth,financeyear,financetype from finance_records where usercode = #{param.usercode,jdbcType=VARCHAR} GROUP BY
financemonth,financeyear,financetype order by
financemonth ${param.order}

select sum(financemoney) financemoney,financequarter,financeyear,financetype from finance_records where usercode = #{param.usercode,jdbcType=VARCHAR} GROUP BY
financequarter,financeyear,financetype order by
financequarter ${param.order}

select sum(financemoney) financemoney,financeyear,financetype from finance_records where usercode = #{param.usercode,jdbcType=VARCHAR} GROUP BY
financeyear,financetype order by
financeyear ${param.order}
